import { useEffect, useState } from 'react';
import axios from "axios";
import { Button, Row, Col, Container, Form } from 'react-bootstrap';
import { Line } from 'react-chartjs-2';
import Select from 'react-select';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../App.css';
// import LineChart from '.LineChart/LineChart';

const baseUrlDataSource = 'http://localhost:3000/api/csv/ds';
const baseURLCompagin = 'http://localhost:3000/api/csv/camp/';




function App() {


  const [dataSource, setdataSource] = useState([]);
  const [dataSourceOptions, setdataSourceOptions] = useState([]);
  const [campaignOptions, setCampaignOptions] = useState([]);


  useEffect(() => {
    axios.get(baseUrlDataSource).then((response) => {
      console.log("data", response.data);
      setdataSource(response.data.data);
    });
  }, [setdataSource]);

  const [campaign, setCampagin] = useState([]);

  useEffect(() => {
    axios.get(baseURLCompagin).then((response) => {
      console.log("sacmpaign", response.data)
      setCampagin(response.data.data);
    });
  }, [setCampagin]);

  const state = {
    labels: dataSource,
    datasets: [
      {
        label: 'Rainfall',
        fill: false,
        lineTension: 0.5,
        backgroundColor: 'rgba(75,192,192,1)',
        borderColor: 'rgba(0,0,0,1)',
        borderWidth: 2,
        data: campaign
      }
    ]
  }

  return (
    <div>
      <Container fluid>
        <Row className="mx-0 mt-5">
          <Col md={4} className="menu">
            <Form.Label>Data Source</Form.Label>
            <Select
              isMulti
              name="colors"
              options={dataSource}
              className="basic-multi-select"
              classNamePrefix="select"
            />

            <Form.Label className="mt-3">Compaign</Form.Label>
            <Select
              name="colors"
              options={campaign}
              className="basic-multi-select"
              classNamePrefix="select"
            />

            <Button variant="primary" className=" mt-3">Apply</Button>

          </Col>
          <Col md={8} className="main">
            <h2>Dashboard</h2>
            <Line
              data={state}
              options={{
                title: {
                  display: true,
                  text: 'Average Rainfall per month',
                  fontSize: 20
                },
                legend: {
                  display: true,
                  position: 'right'
                }
              }}
            />
            {/* <LineChart/> */}
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default App;
